mapper.update:
UPDATE board
SET subject = #{subject}, content = #{content}
WHERE article_no = #{articleNo}

mapper.select:
SELECT article_no, user_id, subject, content, hit, reg_time
FROM board
WHERE article_no = #{articleNo}

mapper.update:
UPDATE board
SET hit = hit + 1
WHERE article_no = #{articleNo}

mapper.select:
SELECT article_no, user_id, subject, content, hit, reg_time
FROM board
WHERE user_id = #{userId}
    OR subject = #{subject}
